export default class WebSocket {
    url: string;
    accessToken: string;
    options: any;
    webSocket: any;
    webSocketConnection: any;
    isConnected: boolean;
    pongTimeoutRef: any;
    pingIntervalRef: any;
    logEntries: any[];
    constructor(options?: any);
    ping(): void;
    pongTimeout(): void;
    clearPongTimeout(): void;
    clearPingInterval(): void;
    onError(err: any): void;
    onMessage(payload: any): void;
    onClose(event: any): void;
    onConnect(connection: any): void;
    connect(): void;
    logRecurring(message: any, severity?: string): void;
    send(data: any, cb: any): void;
    disconnect(): void;
}
